import React, { useState } from 'react';
import './App.css';

function App() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [page, setPage] = useState('home');
  const [timelineEvents, setTimelineEvents] = useState([
    { date: '23 February 2025', description: 'Made it official' },
    { date: '4 March 2025', description: 'Went on our first date' },
    { date: '2023-2024', description: 'Nationals' },
    { date: '25 March 2025', description: "Emy\'s First Birthday with Ron" },
    { date: '29 May 2025', description: 'Emy Graduation Night with Ron' },
    { date: '15-16 June 2025', description: 'Gas Motor Show Together' },
    { date: '22 June 2025', description: 'First Proper Nap' },
  ]);
  const [newEvent, setNewEvent] = useState({ date: '', description: '' });

  const addTimelineEvent = () => {
    if (newEvent.date && newEvent.description) {
      setTimelineEvents([...timelineEvents, newEvent]);
      setNewEvent({ date: '', description: '' });
    }
  };

  const renderHomePage = () => (
    <div className="home-fancy">
      <h1>Welcome to Our Love App!</h1>
      <p>Use the menu to explore our journey together.</p>
      <div className="photo-gallery">
        {/* Placeholder for your beautiful photos! */}
        <div className="photo-placeholder">Photo 1</div>
        <div className="photo-placeholder">Photo 2</div>
        <div className="photo-placeholder">Photo 3</div>
      </div>
      <p className="home-quote">“I Love You The Most And Always Will -Love Emy”</p>
    </div>
  );

  const renderTimeline = () => (
    <div className="timeline-container">
      <h2>Our Timeline</h2>
      <div className="timeline">
        {timelineEvents.map((event, idx) => (
          <div className="timeline-month" key={idx}>
            <div className="timeline-dot"></div>
            <div className="timeline-content">
              <h3>{event.date}</h3>
              <p>{event.description}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="add-event-form">
        <input
          type="text"
          placeholder="Date (e.g. 1 July 2025)"
          value={newEvent.date}
          onChange={e => setNewEvent({ ...newEvent, date: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newEvent.description}
          onChange={e => setNewEvent({ ...newEvent, description: e.target.value })}
        />
        <button className="add-month-btn" onClick={addTimelineEvent}>+ Add Event</button>
      </div>
    </div>
  );

  const renderPage = () => {
    switch(page) {
      case 'timeline':
        return renderTimeline();
      case 'memories':
        return <div>Memories (pics & descriptions)</div>;
      case 'reasons':
        return <div>Reasons I Love You (add more reasons)</div>;
      default:
        return renderHomePage();
    }
  };

  return (
    <div className="App">
      <div className="burger-highlight"></div>
      <button className="burger" onClick={() => setMenuOpen(!menuOpen)}>
        &#9776;
      </button>
      {menuOpen && (
        <nav className="menu">
          <ul>
            <li onClick={() => {setPage('home'); setMenuOpen(false);}}>Home</li>
            <li onClick={() => {setPage('timeline'); setMenuOpen(false);}}>Timeline</li>
            <li onClick={() => {setPage('memories'); setMenuOpen(false);}}>Memories</li>
            <li onClick={() => {setPage('reasons'); setMenuOpen(false);}}>Reasons I Love You</li>
          </ul>
        </nav>
      )}
      <main>
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
